<?phpnamespace App\Mail;//use App\Config;use Illuminate\Bus\Queueable;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;use Illuminate\Contracts\Queue\ShouldQueue;use Illuminate\Http\Request;class SendingMailToMe extends Mailable{    use Queueable, SerializesModels;    /**     * Create a new message instance.     *     * @return void     */    public function __construct()    {        //    }    /**     * Build the message.     *     * @return $this     */    public function build(Request $request)    {        $emailReceive = "info@smartlinks.vn";        $subjectReceive = "YÊU CẦU BÁO GIÁ THIẾT KẾ WEBSITE SMARTWEBVN.COM";        $fromReceive = "smartwebvn.com";        $type = $request->type;        $typeString = '';        if ($type == 1) {            $typeString = 'Gói Cơ Bản';        }        if ($type == 2) {            $typeString = 'Gói Chuyên Nghiệp';        }        if ($type == 3) {            $typeString = 'Gói Nâng Cao';        }        if ($type == 4) {            $typeString = 'Gói Cao Cấp';        }        return $this->view('mail.mail-to-me', ['name' => $request->name, 'email' => $request->email, 'phone' => $request->phone, 'note' => $request->note,'typeString'=>$typeString])->to($emailReceive)->subject($subjectReceive)->from('nnduyquang@gmail.com', $fromReceive);    }}